function setStorageLocal(t,e){return new Promise(function(n,o){let c={};c[t]=e,chrome.storage.local.set(c,function(){n(e)})})}document.addEventListener("DOMContentLoaded",function(){document.getElementById("ptGoAmazon").addEventListener("click",function(t){setStorageLocal("open_amazon",1).then(function(){chrome.tabs.query({active:!0,currentWindow:!0},function(t){var e=t[0];chrome.tabs.update(e.id,{url:"https://www.amazon.com.tr"})})})})});
